    </el-header>
    <transition :name="transitionName">
      <keep-alive>
        <router-view v-if="$route.meta.keepAlive"></router-view>
      </keep-alive>
    </transition>
    <transition :name="transitionName">
      <router-view v-if="! $route.meta.keepAlive"></router-view>
    </transition>
  </el-container>
</template>

<script>
const { remote, ipcRenderer } = require('electron');
import {mapActions, mapGetters} from 'vuex';

const window = remote.getCurrentWindow()
const app = remote.app

export default {
  name: "MainPage",
  data() {
    return {
      closeDialogVisible: false,
      isTop: window.isAlwaysOnTop(),
      transitionName: 'main-slide-left',
    }
  },
  watch: {
    $route(to, from) {
      const toDepth = to.meta.depth
      const fromDepth = from.meta.depth
      this.transitionName = toDepth < fromDepth ? 'main-slide-right' : 'main-slide-left'
    }
  },
  computed: {
    ...mapGetters('Window', ['secondPageTitle']),
  },
  mounted() {

  },
  methods: {
    ...mapActions('Window', ['setTop']),
    top() {
      this.setTop()
      this.isTop = window.isAlwaysOnTop()
    },
    goBack() {
      this.$router.back()
    },
    close() {
      this.$confirm('是否退出应用？', '提示', {
        confirmButtonText: '退出应用',
        cancelButtonText: '最小化到系统托盘',
        type: 'warning'
      }).then(() => {
        ipcRenderer.send('close-app')
      }).catch(() => {

      });
    },
    setting() {
      if (this.$route.name !== 'setting')
        this.$router.push({name: 'setting'})
      else
        this.$router.back()
    },
    max() {
      if (window.isMaximized())
        window.unmaximize()
      else
        window.maximize()
    },
    fullScreen() {
      window.setFullScreen(! window.isFullScreen())
    },
    min() {
      window.minimize()
    },
  }
}
</script>

<style scoped lang="scss">
.main {
  width: 100%;
  height: 100%;
  border-radius: 16px;
  display: flex;
  flex-direction: column;
  .status-bar {
    margin-top: 4px;
    width: 100%;
    box-shadow: 0 5px 8px -5px rgba(0, 0, 0, 0.1);
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    -webkit-app-region: drag;
    .bar-left {
      margin-left: 12px;
      display: flex;
      flex-direction: row;
      align-items: center;
      .title {
        height: 60px;
        display: flex;
        justify-content: center;
        align-items: center;
        color: #42b983;
        font-size: 28px;
        font-weight: 600;
        font-family: 'AlexBrush', serif;
      }
    }
    .detail-page-header {
      -webkit-app-region: drag;
      ::v-deep.el-page-header__left {
        -webkit-app-region: no-drag;
      }
    }
    .button-group {
      height: 100%;
      margin-right: 12px;
      display: flex;
      flex-direction: row;
      align-items: center;
      -webkit-app-region: no-drag;
      .button {
        margin-left: 32px;
        width: 16px;
        height: 16px;
        cursor: pointer;
        .icon {
          width: 16px;
          height: 16px;
        }
      }
      .top {
        margin-left: 32px;
        width: 24px;
        height: 24px;
        -webkit-app-region: no-drag;
        background-color: #42b983;
        border-radius: 4px;
        cursor: pointer;
        color: #fff;
        .icon {
          margin: 3px;
          width: 18px;
          height: 18px;
        }
      }
      .not-top {
        margin-left: 32px;
        width: 24px;
        height: 24px;
        -webkit-app-region: no-drag;
        cursor: pointer;
        .icon {
          margin: 3px;
          width: 18px;
          height: 18px;
        }
      }
    }
  }
  .main-slide-right-enter-active,
  .main-slide-right-leave-active,
  .main-slide-left-enter-active,
  .main-slide-left-leave-active {
    position: absolute;
    top: 64px;
    height: calc(100% - 64px);
    width: 100%;
    will-change: transform;
    transition: all 0.3s ease-out;
  }
  .main-slide-right-enter {
    opacity: 0;
    transform: translate(-100%, 0);
  }
  .main-slide-right-leave-active {
    opacity: 0;
    transform: translate(0%, 0);
  }
  .main-slide-left-enter {
    opacity: 0;
    transform: translate(100%, 0);
  }
  .main-slide-left-leave-active {
    opacity: 0;
    transform: translate(0%, 0);
  }
}
</style>
